SELECT t.tablename, indexname, c.reltuples AS num_rows, pg_size_pretty(pg_relation_size(t.schemaname||'.'||t.tablename::text)) AS table_size, pg_size_pretty(pg_relation_size(t.schemaname||'.'||indexrelname::text)) AS index_size, CASE WHEN indisunique THEN 'Y' ELSE 'N' END AS UNIQUE, idx_scan AS number_of_scans,  idx_tup_read AS tuples_read,  idx_tup_fetch AS tuples_fetched FROM pg_tables t LEFT OUTER JOIN pg_class c ON t.tablename=c.relname  LEFT OUTER JOIN ( SELECT c.relname AS ctablename, ipg.relname AS indexname, x.indnatts AS number_of_columns, idx_scan, idx_tup_read, idx_tup_fetch, indexrelname, indisunique FROM pg_index x JOIN pg_class c ON c.oid = x.indrelid JOIN pg_class ipg ON ipg.oid = x.indexrelid  JOIN pg_stat_all_indexes psai ON x.indexrelid = psai.indexrelid and psai.schemaname not in ( 'pg_catalog','pg_toast')  ) AS foo ON t.tablename = foo.ctablename  ORDER BY idx_scan , index_size desc LIMIT 20;